import React, { useState } from 'react';
// useEffect
import './App.css';
import { useSelector, useDispatch } from "react-redux";
import { fetchWeather } from "./redux/actions/fetchWeather";
import data from "./state.json";

function App() {
  const [city, setCity] = useState("");
        // [state, setState] = useState("");

  const weatherSelector = useSelector((state) => state.weatherinfor);

  const dispatch = useDispatch();
  const getWeatherInfoAction = (city) => dispatch(fetchWeather(city));  

  const getWeatherInfor = (e) => {
    e.preventDefault();

    if (city === "") {
      console.log('no city to search for');
    } else {
      getWeatherInfoAction(city)
    }

  };

  console.log(weatherSelector);

  let detailWeather = "";

  if (weatherSelector) {
    detailWeather = (
<div className="container">
  <div className="weather-side">
    <div className="weather-gradient" />
    <div className="date-container">
      <h2 className="date-dayname">Tuesday</h2><span className="date-day">15 Jan 2019</span><span className="location">Paris, FR</span>
    </div>
    <div className="weather-container">
      <h1 className="weather-temp">29°C</h1>
      <h3 className="weather-desc">Sunny</h3>
    </div>
  </div>
  <div className="info-side">
    <div className="today-info-container">
      <div className="today-info">
        <div className="precipitation"> <span className="title">PRECIPITATION</span><span className="value">0 %</span>
          <div className="clear" />
        </div>
        <div className="humidity"> <span className="title">HUMIDITY</span><span className="value">34 %</span>
          <div className="clear" />
        </div>
        <div className="wind"> <span className="title">WIND</span><span className="value">0 km/h</span>
          <div className="clear" />
        </div>
      </div>
    </div>
    <div className="week-container">
      <ul className="week-list">
        <li className="active"><span className="day-name">Tue</span><span className="day-temp">29°C</span></li>
        <li><span className="day-name">Wed</span><span className="day-temp">21°C</span></li>
        <li><span className="day-name">Thu</span><span className="day-temp">08°C</span></li>
        <li><span className="day-name">Fry</span><span className="day-temp">19°C</span></li>
        <div className="clear" />
      </ul>
    </div>
  </div>
</div>
    )
  } else {
    <div className="details"> You need to choose state. </div>
  }

  return (
    <div className="App">
      <h3 className="text-white"><center>REACT-REDUX WEATHER APP</center></h3>
      <br/>
      <form onSubmit={getWeatherInfor}>
        <div className="form-row">
          <div className="btn-group col-12">
            <select id="inputState" className="form-control col-9 bg-dark text-white" value={city} onChange={e => setCity(e.target.value)}>
              <option value="">Choose State</option>
              {
                data.map(val => {
                  return (
                    <option value={val.id} key={val.id}>{val.name}</option>
                  )
                })
              }
            </select>
            <button type="submit" className="btn-dark col-3 btn-md location-button" >Check Weather</button>
          </div>
        </div>
      </form>

      <br/>
      <br/>

      { detailWeather }
    </div>
  );
}

export default App;
