import React, { useState } from 'react';
// useEffect
import { useSelector, useDispatch } from "react-redux";
import { fetchWeather } from "./redux/actions/fetchWeather";
import data from "./state.json";

function App() {
  const [city, setCity] = useState("");
        // [state, setState] = useState("");

  const weatherSelector = useSelector((state) => state);

  console.log(weatherSelector.weatherinfor);

  const dispatch = useDispatch();
  const getWeatherInfoAction = (city) => dispatch(fetchWeather(city));  

  const getWeatherInfor = (e) => {
    e.preventDefault();

    if (city === "") {
      console.log('no city to search for');
    } else {
      getWeatherInfoAction(city)
    }

  };

  let detailWeather = "";

  if (weatherSelector.weatherinfor) {
    detailWeather = (<div class="container">
    <div class="weather-side">
        <div class="weather-gradient"></div>
        <div class="date-container">
            <h2 class="date-dayname">Tuesday</h2><span class="date-day">15 Jan 2019</span><i class="location-icon" data-feather="map-pin"></i><span class="location">Paris, FR</span>
        </div>
        <div class="weather-container"><i class="weather-icon" data-feather="sun"></i>
            <h1 class="weather-temp">29°C</h1>
            <h3 class="weather-desc">Sunny</h3>
        </div>
    </div>
    <div class="info-side">
        <div class="today-info-container">
            <div class="today-info">
                <div class="precipitation"> <span class="title">PRECIPITATION</span><span class="value">0 %</span>
                    <div class="clear"></div>
                </div>
                <div class="humidity"> <span class="title">HUMIDITY</span><span class="value">34 %</span>
                    <div class="clear"></div>
                </div>
                <div class="wind"> <span class="title">WIND</span><span class="value">0 km/h</span>
                    <div class="clear"></div>
                </div>
            </div>
        </div>
        <div class="week-container">
            <ul class="week-list">
                <li class="active"><i class="day-icon" data-feather="sun"></i><span class="day-name">Tue</span><span class="day-temp">29°C</span></li>
                <li><i class="day-icon" data-feather="cloud"></i><span class="day-name">Wed</span><span class="day-temp">21°C</span></li>
                <li><i class="day-icon" data-feather="cloud-snow"></i><span class="day-name">Thu</span><span class="day-temp">08°C</span></li>
                <li><i class="day-icon" data-feather="cloud-rain"></i><span class="day-name">Fry</span><span class="day-temp">19°C</span></li>
                <div class="clear"></div>
            </ul>
        </div>
        <div class="location-container"><button class="location-button"> <i data-feather="map-pin"></i><span>Change location</span></button></div>
    </div>
</div>
    )
  } else {
    <div className="details"> You need to choose state. </div>
  }

  return (
    <div className="container">
      <h3><center>REACT-REDUX WEATHER APP</center></h3>
      <br/>
      <form onSubmit={getWeatherInfor}>
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="inputState">State</label>
            <select id="inputState" className="form-control" value={city} onChange={e => setCity(e.target.value)}>
              <option value="">Choose State</option>
              {
                data.map(val => {
                  return (
                    <option value={val.id} key={val.id}>{val.name}</option>
                  )
                })
              }
            </select>
          </div>
        </div>
        <button type="submit" className="btn-dark btn-sm">Check Weather</button>
        <br/>
        { detailWeather }
      </form>
    </div>
  );
}

export default App;
